import React, { Component } from "react";
// import "./app.css";
import { observer, inject } from "mobx-react";
@inject("store")
@observer
class Prodect extends Component {
  constructor() {
    super();
    this.state = {
      messege: false,
      messageText: ""
    };
  }
  addToCart = () => {
    this.setState({
      messageText: "item added succsusfuly",
      messege: true
    });
    let productId = this.props.data.id;
    let userId = this.props.userId;
    this.props.store.userAddProduct(userId, productId);
    this.props.getProducts();
    setTimeout(() => {
      this.setState({
        messege: false,
        messageText: ""
      });
    }, 2000);
  };
  deleteItem = () => {
    this.props.store.deleteItem(this.props.data.id).then(data => {
      if (data.data === true) {
        this.props.getProducts();
      }
    });
  };
  render() {
    let data = this.props.data;
    let border = "";
    for (let i in data.customers) {
      let user = data.customers[i];
      if (user.id === this.props.userId) {
        border = "10px green solid";
      }
    }
    return (
      <div style={{ border: border }} className="prodect">
        {this.state.messege ? (
          <div className="success-message">{this.state.messageText}</div>
        ) : null}
        <div
          style={{ backgroundImage: `url(${data.image})` }}
          className="product-image"
        />
        <h2>{data.name}</h2>
        <h2>{data.price + "$"}</h2>
        <p>{data.description}</p>
        <h2>{data.minValid + " to close.."}</h2>
        <button onClick={this.deleteItem} className="delete-product">
          delete
        </button>
        <button onClick={this.addToCart} className="add-product">
          add
        </button>
      </div>
    );
  }
}

export default Prodect;
