import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Register from "./components/Register";
import LogIn from "./components/LogIn";
import Home from "./components/Home";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
@inject("store")
@observer
class App extends Component {
  constructor() {
    super();
    this.state = {
      redirectTo: null,
      userInfo: null
    };
  }
  componentDidMount() {
    let data = this.props.store.initUser();
    let logIn = JSON.parse(localStorage.getItem("user"));
    if (logIn) {
      this.setState({
        redirectTo: "/home"
      });
    }
  }
  render() {
    console.log(this.props.store.userInfo.name);
    if (this.props.store.userInfo.name === "guy") {
      this.setState({
        redirectTo: "/alon"
      });
    } else {
      console.log("fuck");
    }

    if (this.state.userInfo !== null) {
    }
    return (
      <Router>
        <div>
          <Route
            exact
            path="/"
            render={() => {
              return <Register />;
            }}
          />
          <Route
            exact
            path="/LogIn"
            render={() => {
              return <LogIn />;
            }}
          />
          <Route
            path="/home"
            render={() => {
              return <Home data={this.state.userInfo} />;
            }}
          />
        </div>
      </Router>
    );
  }
}

export default App;
