import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";

@inject("store")
@observer
class Register extends Component {
  constructor() {
    super();
    this.state = {
      newUser: { Name: "", Email: "", Password: "", Phone: "" }
    };
  }
  getInfo = event => {
    let user = this.state.newUser;
    user[event.target.name] = event.target.value;
    this.setState({
      newUser: user
    });
  };
  addUser = () => {
    this.props.Store.addUser(this.state.newUser);
  };
  render() {
    return (
      <div className="register">
        <div className="register1">
          <div className="register-header">
            <Link to="/LogIn">
              <span className="sign-in">Sign in</span>
            </Link>
          </div>
          <h3>
            using this platform to sell/buy with the assist of purchase groups,
            you can sign in as customer or seller and immediately join the big
            market
          </h3>
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Name"
            placeholder="Name"
          />
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Email"
            placeholder="Email"
          />
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Password"
            placeholder="Password"
          />
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Phone"
            placeholder="Phone"
          />
          <button onClick={this.addUser}>create</button>
        </div>
      </div>
    );
  }
}

export default Register;
