import React, { Component } from "react";
import axios from "axios";
// import "./app.css";
import { BrowserRouter as Router, Redirect, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
@inject("store")
@observer
class Home extends Component {
  constructor() {
    super();
    this.state = {
      redirectTo: null,
      userInfo: {}
    };
  }
  async componentDidMount() {
    let logIn = JSON.parse(localStorage.getItem("user"));
    if (!logIn) {
      this.setState({
        redirectTo: "/LogIn"
      });
    } else {
      let answer = await axios.get("/initUser");
      let data = {
        name: answer.data.name,
        email: answer.data.email,
        phone: answer.data.phone
      };
      this.setState({
        userInfo: data
      });
    }
  }
  logout = () => {
    this.props.store.userLogOut(this.props.store.getUser.id).then(() => {
      this.setState({
        redirectTo: "/logIn"
      });
    });
  };
  render() {
    console.log(toJS(this.state.userInfo));
    if (this.state.redirectTo === null) {
      setTimeout(function() {}, 3000);
      return (
        <div className="home">
          <div className="nav-bar">
            <ul>
              <li className="name">
                <a href="#">{this.state.userInfo.name}</a>
              </li>
              <li>
                <a href="#">Home</a>
              </li>
            </ul>
          </div>
          <button onClick={this.logout}>logout</button>
        </div>
      );
    } else {
      return <Redirect to={{ pathname: this.state.redirectTo }} />;
    }
  }
}

export default Home;
