import React, { Component } from "react";
import axios from "axios";
// import "./app.css";
import { BrowserRouter as Router, Redirect, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
import Seller from "./Seller";
@inject("store")
@observer
class Home extends Component {
  constructor() {
    super();
    this.state = {
      redirectTo: null,
      userInfo: {},
      products: []
    };
  }
  changeRedirect = () => {
    this.setState({
      redirectTo: "/login"
    });
  };
  async componentDidMount() {
    let logIn = JSON.parse(localStorage.getItem("user"));
    if (!logIn) {
      this.setState({
        redirectTo: "/LogIn"
      });
    } else {
      if (this.props.store.userInfo === undefined || null || {}) {
        let answer = await axios.get("/initUser");
        let products = await axios.get("/products");
        let data = {
          name: answer.data.name,
          email: answer.data.email,
          phone: answer.data.phone,
          seller: answer.data.seller,
          id: answer.data.id,
          products: answer.data.products
        };
        this.setState({
          userInfo: data
        });
      } else {
        setTimeout(() => {
          let answer = axios.get("/initUser");
          let data = {
            name: answer.data.name,
            email: answer.data.email,
            phone: answer.data.phone,
            seller: answer.data.seller,
            id: answer.data.id,
            products: answer.data.products
          };
          this.setState({
            userInfo: data
          });
        }, 1000);
      }
    }
  }

  logoutF = () => {
    this.setState({
      redirectTo: "/logIn"
    });
  };
  render() {
    if (this.state.userInfo !== {}) {
      if (this.state.redirectTo === null) {
        return (
          <Seller
            changeRedirect={this.changeRedirect}
            userInfo={this.state.userInfo}
            logoutF={this.logoutF}
          />
        );
      } else {
        return <Redirect to={{ pathname: this.state.redirectTo }} />;
      }
    } else {
      console.log("guy");
      return <div />;
    }
  }
}

export default Home;
