import React, { Component } from "react";
import axios from "axios";
// import "./app.css";
import { BrowserRouter as Router, Redirect, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
import Seller from "./Seller";
@inject("store")
@observer
class Home extends Component {
  constructor() {
    super();
    this.state = {
      redirectTo: null,
      userInfo: {}
    };
  }
  changeRedirect = () => {
    this.setState({
      redirectTo: "/login"
    });
  };
  async componentDidMount() {
    let logIn = JSON.parse(localStorage.getItem("user"));
    if (!logIn) {
      this.setState({
        redirectTo: "/LogIn"
      });
    } else {
      let answer = await axios.get("/initUser");
      let data = {
        name: answer.data.name,
        email: answer.data.email,
        phone: answer.data.phone,
        seller: answer.data.seller,
        id: answer.data.id,
        products: answer.data.products
      };
      console.log(data);
      this.setState({
        userInfo: data
      });
    }
  }

  SellerOrCustomer = () => {
    let data = this.state.userInfo;
    console.log(data.seller === "0");
    if (data !== undefined) {
      return (
        <Seller
          changeRedirect={this.changeRedirect}
          userInfo={this.state.userInfo}
          logoutF={this.logoutF}
        />
      );
    } else {
      return <div />;
    }
  };
  logoutF = () => {
    this.setState({
      redirectTo: "/logIn"
    });
  };
  render() {
    if (this.state.userInfo.seller === "1") {
      console.log(this.state.userInfo.seller);
    }
    if (this.state.redirectTo === null) {
      return <this.SellerOrCustomer />;
    } else {
      return <Redirect to={{ pathname: this.state.redirectTo }} />;
    }
  }
}

export default Home;
