import React, { Component } from "react";
import axios from "axios";
// import "./app.css";
import { BrowserRouter as Router, Redirect, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
@inject("store")
@observer
class Prodect extends Component {
  constructor() {
    super();
    this.state = {
      info: []
    };
  }

  test = () => {
    if (this.props.prodect !== {}) {
      let data = this.props.prodect;
      let prodects = this.state.info;
      prodects.map(prod => {
        if (prod.id === data.id) {
          return (
            <div>
              <div
                style={{ backgroundImage: `url(${prod.image})` }}
                className="product-image"
              />
              <h2>{prod.name}</h2>
              <h2>yours</h2>
              <h2>{prod.price + "$"}</h2>
              <h2>{prod.minValid + " to close.."}</h2>
              <button className="delete-product">delete</button>
            </div>
          );
        } else {
          return (
            <div>
              <div
                style={{ backgroundImage: `url(${prod.image})` }}
                className="product-image"
              />
              <h2>{prod.name}</h2>
              <h2>{prod.price + "$"}</h2>
              <h2>{prod.minValid + " to close.."}</h2>
              <button className="delete-product">delete</button>
            </div>
          );
        }
      });
    } else {
      return <div />;
    }
  };
  render() {
    console.log(this.state.info);
    let data = this.props.prodect;
    let size = this.props.arraySize;
    return (
      <div className="prodect">
        <this.test />
      </div>
    );
  }
}

export default Prodect;
