import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";

@inject("store")
class Register extends Component {
  constructor() {
    super();
    this.state = {
      newUser: { Name: "", Email: "", Password: "", Phone: "", error: false }
    };
  }
  getInfo = event => {
    let user = this.state.newUser;
    user[event.target.name] = event.target.value;
    this.setState({
      newUser: user
    });
  };
  addNewUser = () => {
    let user = this.state.newUser;
    if (user.Name.length < 3) {
      console.log("fix it");
    } else if (user.Email.length < 11) {
      console.log("fix it");
    } else if (user.Password.length < 8) {
      console.log("fix it");
    } else if (user.Phone.length < 8) {
      console.log("fix it");
    } else {
      this.props.store.addUser(this.state.newUser);
    }
  };
  render() {
    return (
      <div className="register">
        <div className="register1">
          <div className="register-header">
            <Link to="/LogIn">
              <span className="sign-in">Sign in</span>
            </Link>
          </div>
          <h3>
            using this platform to sell/buy with the assist of purchase groups,
            you can sign in as customer or seller and immediately join the big
            market
          </h3>
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Name"
            placeholder="Name (min-4 Letters)"
          />
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Email"
            placeholder="Email (min-11 Letters)"
          />
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Password"
            placeholder="Password (min-8 Letters)"
          />
          <input
            onChange={event => {
              this.getInfo(event);
            }}
            name="Phone"
            placeholder="Phone (min-8 Letters)"
          />
          <button onClick={this.addNewUser}>create</button>
        </div>
      </div>
    );
  }
}

export default Register;
