import { observable, action } from "mobx";
import axios from "axios";

class Store {
  @observable
  iceCreams = [];

  @action
  addUser(userInfo) {
    let answer = axios.post("/newUser", userInfo).then(response => {
      if (response.status === 200) {
        return true;
      }
    });
  }
}

const store = new Store();
export default store;
