import React, { Component } from "react";
import axios from "axios";
// import "./app.css";
import { BrowserRouter as Router, Redirect, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
@inject("store")
@observer
class Prodect extends Component {
  constructor() {
    super();
    this.state = {
      info: []
    };
  }
  async componentDidMount() {
    let answer = await this.props.store.getAllProducts();
    this.setState({
      info: answer.data
    });
  }
  test = () => {
    if (this.state.info !== []) {
      let data = this.props.prodect;
      let prodects = this.state.info;
      data.map(prod => {
        if (prod.id === data.id) {
          console.log("works");
        }
      });
    }
  };
  render() {
    console.log(this.state.info);
    let data = this.props.prodect;
    let size = this.props.arraySize;
    this.test();
    return (
      <div className="prodect">
        <div
          style={{ backgroundImage: `url(${data.image})` }}
          className="product-image"
        />
        <h2>{data.name}</h2>
        <h2>{data.price + "$"}</h2>
        <h2>{data.minValid + " to close.."}</h2>
        <button className="delete-product">delete</button>
      </div>
    );
  }
}

export default Prodect;
