import React, { Component } from "react";
import axios from "axios";
// import "./app.css";
import { BrowserRouter as Router, Redirect, Link } from "react-router-dom";
import { observer, inject } from "mobx-react";
import { toJS } from "mobx";
@inject("store")
@observer
class Home extends Component {
  constructor() {
    super();
    this.state = {
      redirectTo: null,
      userInfo: {}
    };
  }
  async componentDidMount() {
    let answer = await axios.get("/initUser");
    let data = {
      name: answer.data.name,
      email: answer.data.email,
      phone: answer.data.phone
    };
    console.log(toJS(this.props.store.userInfo));
    let logIn = JSON.parse(localStorage.getItem("user"));
    if (!logIn) {
      this.setState({
        redirectTo: "/LogIn",
        userInfo: data
      });
    } else {
      //   this.props.store.initialUser().then(() => {
      //     this.setState({
      //       currentUser: this.props.store.userInfo
      //     });
      //   });
      this.setState({
        userInfo: this.props.store.userInfo
      });
    }
  }
  logout = () => {
    this.props.store.userLogOut(this.props.store.getUser.id).then(() => {
      this.setState({
        redirectTo: "/logIn"
      });
    });
  };
  render() {
    if (this.state.redirectTo === null) {
      setTimeout(function() {}, 3000);
      return (
        <div>
          <h1>{this.state.userInfo.name}</h1>
          <h1>{this.state.userInfo.phone}</h1>
          <button onClick={this.logout}>logout</button>
        </div>
      );
    } else {
      return <Redirect to={{ pathname: this.state.redirectTo }} />;
    }
  }
}

export default Home;
