import { observable, action, computed } from "mobx";
import axios from "axios";

class Store {
  @observable
  userInfo = {};
  @action
  currentUser(info) {
    this.userInfo = info;
  }
  @computed
  get getUser() {
    return this.userInfo;
  }
  @action
  async addUser(userInfo) {
    let answer = await axios.post("/newUser", userInfo);
    if (answer.data.exist === true) {
      this.currentUser(answer.data.userInfo);
      localStorage.setItem("user", "true");
    }
    return answer.data.exist;
  }
  @action
  async userLogIn(userInfo) {
    let answer = await axios.get("/LogIn", {
      params: { name: userInfo.Name, password: userInfo.Password }
    });
    if (answer.data.exist === true) {
      this.currentUser(answer.data.userInfo);
      localStorage.setItem("user", "true");
      return true;
    } else {
      return null;
    }
  }
  @action
  async userLogOut(userId) {
    let answer = await axios.put("/logOut", { userId });
    if (answer.data === true) {
      this.currentUser({});
      localStorage.clear();
    }
    return answer.data;
  }
  @action
  async initialUser() {
    let answer = await axios
      .get("/initUser")
      .then(data => {
        this.currentUser(data);
      })
      .then(() => {
        console.log(this.userInfo);
      });
    localStorage.setItem("user", "true");
  }
}

const store = new Store();
window.store = store;
export default store;
