import React, { Component } from "react";
import axios from "axios";
import "./App.css";
import { BrowserRouter as Router, Route, Redirect } from "react-router-dom";
import Register from "./components/Register";
import LogIn from "./components/LogIn";
import User from "./components/User";
import { observer, inject } from "mobx-react";
import { throws } from "assert";
import { toJS } from "mobx";

@inject("store")
@observer
class App extends Component {
  constructor() {
    super();
    this.state = {
      redirectTo: null,
      userInfo: {},
      products: {}
    };
  }
  redirectTo = data => {
    console.log(data);
    this.setState({
      redirectTo: `"${data}"`
    });
  };
  async componentDidMount() {
    let logIn = JSON.parse(localStorage.getItem("user"));
    if (logIn) {
      await this.props.store.initUser();
      await this.props.store.getAllProducts();
      this.setState({
        userInfo: toJS(this.props.store.userInfo),
        redirectTo: "/home"
      });
    }
  }
  render() {
    // console.log(toJS(this.props.store.userInfo));
    if (this.state.redirectTo === null) {
      return (
        <Router>
          <div>
            <Route
              exact
              path="/"
              render={() => {
                return <Register />;
              }}
            />
            <Route
              exact
              path="/LogIn"
              render={() => {
                return <LogIn />;
              }}
            />
            <Route
              path="/home"
              render={() => {
                return (
                  <User
                    redirectTo={this.redirectTo}
                    userInfo={this.props.store.userInfo}
                    products={this.props.store.products}
                  />
                );
              }}
            />
          </div>
        </Router>
      );
    } else {
      return <Redirect to={{ pathname: this.state.redirectTo }} />;
    }
  }
}

export default App;
